import { configureStore, createSlice } from '@reduxjs/toolkit';

const initialCartButtonState = { toggleCart: false }

const cartButtonSlice = createSlice({

    name: 'cartButton',
    initialState: initialCartButtonState,
    reducers: {
        toggleCart(state) {
            state.toggleCart = !state.toggleCart
        }
    }

})

const initialCartState = { cartTotalAmount: 0, cartItem: {} }
const cartSlide = createSlice({
    name: 'cart',
    initialState: initialCartState,
    reducers: {
        addToCart(state, action) {
            state.cartItem.title = action.payload.title
            state.cartItem.quantity = action.payload.quantity
            state.cartItem.price = action.payload.price

        }
    }
})

const store = configureStore({
    reducer: { cartButton: cartButtonSlice.reducer }
})

export const cartActions = cartButtonSlice.actions;
export default store;
