import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector, useDispatch } from 'react-redux';
import { useEffect } from 'react'
import Notification from './components/UI/Notification';
import { cartButtonActions } from './store';

function App() {
  const cartToggle = useSelector((state) => state.cartButton.toggleCart)
  const cart = useSelector(state => state.cart)
  const dispatch = useDispatch();

  useEffect(() => {

    if (cart.cartItems.length > 0) {
      const fetchData = async () => {
        dispatch(cartButtonActions.showNotification({
          status: 'pending',
          title: 'Sending...',
          message: 'Sending cart data!'
        }))
        try {
          const response = await fetch('https://cartreact-342a6-default-rtdb.firebaseio.com/cart.json', {
            method: 'PUT',
            body: JSON.stringify(cart)
          })

          if (!response.ok) {
            throw new Error('Something went wrong!')
          }

          dispatch(cartButtonActions.showNotification({
            status: 'success',
            title: 'Success!',
            message: 'Sent cart data successfully!'
          }))

        } catch (error) {
          dispatch(cartButtonActions.showNotification({
            status: 'error',
            title: 'Error',
            message: 'Sending cart data failed!'
          }))
        }
      }

      fetchData()
    }

  }, [cart,dispatch])

  return (
    <Layout>
      {!cartToggle && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
