import classes from './CartButton.module.css';
import { useDispatch, useSelector } from 'react-redux';
import { cartButtonActions } from '../../store/index';

const CartButton = (props) => {

  const cartToggle = useSelector(state => state.cartButton.toggleCart)
  const dispatch = useDispatch()
  console.log(cartToggle)

  const cartToggleHandler = () => {
    dispatch(cartButtonActions.toggleCart())
  }

  return (
    <button type='button' onClick={cartToggleHandler} className={classes.button}>
      <span>My Cart</span>
      <span className={classes.badge}>1</span>
    </button>
  );
};

export default CartButton;
