import { configureStore, createSlice } from '@reduxjs/toolkit';

const initialCartButtonState = { toggleCart: false }

const cartButtonSlice = createSlice({

    name: 'cartButton',
    initialState: initialCartButtonState,
    reducers: {
        toggleCart(state) {
            state.toggleCart = !state.toggleCart
        }
    }

})

const initialCartState = { totalAmount: 0, cartItems: [] }
const cartSlice = createSlice({
    name: 'cart',
    initialState: initialCartState,
    reducers: {
        addToCart(state, action) {

            const updatedTotalAmount = state.totalAmount + action.payload.price
            const existingCartItemIndex = state.cartItems.findIndex(item => item.id === action.payload.id)
            const existngCartItem = state.cartItems[existingCartItemIndex];
            let updatedItems;

            if (existngCartItem) {
                console.log("var")
                const updatedItem = {
                    ...existngCartItem,
                    amount: existngCartItem.amount + action.payload.amount
                };
                updatedItems = [...state.items];
                updatedItems[existingCartItemIndex] = updatedItem
            }
            else {
                updatedItems = state.cartItems.concat(action.payload);
            }

            state.cartItems = updatedItems
            state.totalAmount = updatedTotalAmount

            // console.log(state.cartItems)
            // console.log(state.totalAmount)

        }
    }
})

const store = configureStore({
    reducer: { 
        cartButton: cartButtonSlice.reducer,
        cart: cartSlice.reducer
    }
})

export const cartActions = cartSlice.actions;
export const cartButtonActions = cartButtonSlice.actions;
export default store;
